                   api_token = pal::pkg_config_val(key = "api_token",
                                                   pkg = this_pkg,
                                                   required = FALSE)) {
  checkmate::assert_string(tbl_name)
  
  result <-
    tbls(id_base = id_base,
         hostname = hostname,
         email = email,
         password = password,
         api_token = api_token) |>
    dplyr::filter(table_name == !!tbl_name) |>
